mapper.insert:
INSERT INTO lc_board (
	b_idx,
	b_title,
	b_content

) VALUES (
	#{bIdx},
	#{bTitle},
	#{bContent}
)

INSERT INTO lc_auth ( u_id, u_auth ) VALUES
(#{username}, #{authority.role})
ON DUPLICATE KEY UPDATE u_id = #{username}

mapper.update:
UPDATE		lc_board
SET			b_title = #{bTitle},
			b_content = #{bContent}

WHERE 		b_idx = #{bIdx}
